mapper.select:
SELECT content_id, content_type_id, title, addr1, addr2, zipcode, tel, first_image, readcount, sido_code, gugun_code, latitude, longitude, like_count
  FROM attraction_info
 WHERE title like #{keyWord}

SELECT content_Id, content_type_id, title, addr1, zipcode, tel, first_image, readcount, sido_code, gugun_code, latitude, longitude, like_count FROM attraction_info WHERE title like CONCAT(#{keyword}, '%')
AND sido_code = #{sido}r
AND gugun_code = #{gugun}
AND content_type_id IN
#{type}

SELECT content_id, content_type_id, title, addr1, addr2, zipcode, tel, first_image, readcount, sido_code, gugun_code, latitude, longitude, like_count
  FROM attraction_info
 WHERE content_id = #{contentId}
