mapper.select:
		SELECT member_id
		  FROM member
         WHERE email = #{email};

SELECT member_id, email, password, nickname, profile_img, bio
  FROM member
 WHERE email = #{email}

SELECT member_id, email, password, nickname, profile_img, bio
FROM member
WHERE member_id = #{memberId}

SELECT member_id, email, nickname, profile_img, bio
  FROM member
 WHERE member_id = #{memberId}

SELECT *
  FROM member
 WHERE email=#{check}
    OR nickname=#{check}

SELECT tl.item_id, ai.title, ai.first_image, ai.like_count, ai.rate
  FROM tb_like tl
  JOIN attraction_info ai
    ON tl.item_id = ai.content_id
 WHERE tl.member_id = #{memberId}

	AND ai.sido_code = #{sido}


	AND ai.gugun_code = #{gugun}

mapper.update:
UPDATE member
SET password = #{password}
WHERE nickname = #{nickname}

UPDATE member
   SET bio = #{bio}
 WHERE member_id = #{memberId}

UPDATE member
   SET profile_img = #{image}
 WHERE member_id = #{memberId}

		UPDATE member
           SET token = #{token}
    	 WHERE email = #{email}

mapper.delete:
UPDATE member
   SET token = #{token, jdbcType=VARCHAR}
 WHERE member_id = #{memberId}
